mapper.update:
update
	buy_user
set
	day = #{day}
	, want = 0
	, inputdate = sysdate
	, cardnum = #{card}
where
	booknum = #{booknum}
	and
	id = #{id}

update
	buy_user
set
	day = day + #{day}
where
	id = #{id}
	and
	booknum = #{booknum}
